CREATE TABLE Cobo_monedas ([codigo] VARCHAR (50) PRIMARY KEY NOT NULL, [url_Inet] VARCHAR (50) NOT NULL, [descripcion] VARCHAR (100) DEFAULT(NULL), [valor] FLOAT (0, 0) DEFAULT(NULL), [fechaRegistro] TIMESTAMP NOT NULL);
CREATE TABLE Cobo_configuracion ([codigo] VARCHAR (50) PRIMARY KEY NOT NULL, [valor] VARCHAR (250) NOT NULL, [descripcion] VARCHAR (250) NOT NULL);
CREATE TABLE Cobo_mercado_moneda ([id] INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, [nombreUrl] VARCHAR (50) NOT NULL, [abrevMoneda] VARCHAR (50) DEFAULT(NULL), [fechaRegistro] DATETIME DEFAULT(NULL));
