CREATE TABLE Cobo_mercados ( mercado VARCHAR ( 20 ) PRIMARY KEY UNIQUE , habilitado BOOLEAN , descripcion VARCHAR ( 200 ) );
CREATE TABLE Cobo_nombreticket ([nombre] VARCHAR (50) PRIMARY KEY NOT NULL, [fechaRegistro] DATE NOT NULL DEFAULT('0000-00-00 00:00:00'), [fechaError] DATETIME DEFAULT(NULL), [fechaActualizacion] DATETIME DEFAULT(NULL), [fechahistorico] DATE DEFAULT(NULL));
CREATE TABLE Cobo_componentes ([codigo] INTEGER PRIMARY KEY NOT NULL, [nombre] VARCHAR (255) DEFAULT(NULL), [tiket] VARCHAR (50) NOT NULL REFERENCES [Cobo_nombreticket] ([nombre]), [mercado] VARCHAR (255) DEFAULT(NULL), [max52] FLOAT (0, 0) DEFAULT(NULL), [maxDia] FLOAT (0, 0) DEFAULT(NULL), [min52] FLOAT (0, 0) DEFAULT(NULL), [minDia] FLOAT (0, 0) DEFAULT(NULL), [valorActual] FLOAT (0, 0) DEFAULT(NULL), [volumenMedio] FLOAT (0, 0) DEFAULT(NULL), [volumen] FLOAT (0, 0) DEFAULT(NULL), [error] VARCHAR (255) DEFAULT(NULL), [fechaRegistro] DATE DEFAULT('0000-00-00 00:00:00'));
CREATE TABLE Cobo_params_operaciones ([id] INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, [precio_ini] FLOAT (0, 0) DEFAULT(0), [precio_fin] FLOAT (0, 0) DEFAULT(0), [fecha_ini] DATETIME DEFAULT(NULL), [fecha_fin] DATETIME DEFAULT(NULL), [timing] CHAR (2) NOT NULL, [precio_salida] FLOAT (0, 0) DEFAULT(0), [salida] FLOAT (0, 0) DEFAULT(0), [entrada] FLOAT (0, 0) DEFAULT(0), [rentabilidad] FLOAT (0, 0) DEFAULT(0), [codigo] INT NOT NULL DEFAULT(0) REFERENCES [Cobo_componentes] ([codigo]));
CREATE TABLE Cobo_nombreticket_borrados ([nombre] VARCHAR (50) PRIMARY KEY NOT NULL, [fechaRegistro] DATE NOT NULL DEFAULT('0000-00-00 00:00:00'));
